function go_shopify_product() {
  var xhttp = new XMLHttpRequest();
  xhttp.onreadystatechange = function() {
    if (this.readyState == 4 && this.status == 200) {
      var product=JSON.parse(this.responseText);
       console.log(product);
      console.log(product.title);
      var priceUsd=product.price/100;
      var discount=priceUsd/5;
      var realPrice=priceUsd-discount;
      document.getElementById("upsell-product-name").innerHTML=product.title;
      document.getElementById("upsell-price").innerHTML="$"+Math.round(product.price/100);
      document.getElementById("upsell-discount-price").innerHTML="$"+Math.round(realPrice)+' USD';
       document.getElementById("describe").innerHTML=product.description;
      document.getElementById("upsell-img-box").innerHTML='<image src="'+product.featured_image +'" />';
      
    }
  };
  var url='https://mykitsch.com//products/'+product_handle+'.js';
  xhttp.open("GET", url, true);
  xhttp.send();
}
go_shopify_product();
function upsell() {
  document.getElementById("upsell-wrapper").style.display='none';
}
